mapper.select:
select 

from book
where book_id = #{bookId,jdbcType=BIGINT}
  and create_time = #{createTime,jdbcType=TIMESTAMP}

mapper.delete:
delete from book
where book_id = #{bookId,jdbcType=BIGINT}
  and create_time = #{createTime,jdbcType=TIMESTAMP}

mapper.insert:
insert into book (book_id, create_time, title, 
  author, image, update_time
  )
values (#{bookId,jdbcType=BIGINT}, #{createTime,jdbcType=TIMESTAMP}, #{title,jdbcType=VARCHAR}, 
  #{author,jdbcType=VARCHAR}, #{image,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update book
set title = #{title,jdbcType=VARCHAR},
  author = #{author,jdbcType=VARCHAR},
  image = #{image,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where book_id = #{bookId,jdbcType=BIGINT}
  and create_time = #{createTime,jdbcType=TIMESTAMP}
